var GlitchText = function(){
    this.random = function(str){
        var ms = this.methods();
        var m = ms[Math.floor(Math.random()*ms.length)];
        return this[m](str);
    };

    this.methods = function(){
        var ms = new Array();
        for(m in this){
            if(m.match(/^glitch_.+/)) ms.push(m);
        };
        return ms;
    };

};

GlitchText.prototype.glitch_addspace = function(str){
    return str.split('').join(' ');
};
GlitchText.prototype.glitch_atai = function(str){
    return "＼"+str+"／";
};

try{
    if(typeof TinySegmenter == 'function'){
        GlitchText.prototype.glitch_cmabridge = function(str){
            var tseg = new TinySegmenter();
            return tseg.segment(str).map(function(seg){
                seg = seg.split('');
                var i = Math.floor(Math.random()*(seg.length-1));
                var tmp = seg[i];
                seg[i] = seg[i+1];
                seg[i+1] = tmp;
                return seg.join('');
            }).join('');
        };
    }
}
catch(e){
}

GlitchText.prototype.glitch_double = function(str){
    return str + " " + str;
};
GlitchText.prototype.glitch_doubleChar = function(str){
    return str.split('').map(function(i){
        if(i.match(/[ 　\t]/)) return i;
        return i+i;
    }).join('');
};
