GlitchText.prototype.glitch_updown = function(str){
    var count = 0;
    return str.split('').map(function(c){
        if(Math.random() > 0.7){
            count++;
            if(count % 2 == 0){
                return c+'↑';
            }
            else{
                return c+'↓';
            }
        };
        return c;
    }).join('');
};

Array.prototype.choice = function(){
    return this[Math.floor(Math.random()*this.length)];
};

Array.prototype.map = function(func){
    var res = new Array();
    this.forEach(function(i){res.push(func(i))});
    return res;
};

GlitchText.prototype.glitch_vertical_reverse = function(str){
    var a = 'abcdefghijklmnopqrstuvwxyz'.split('');
    var b = 'ɐqɔpǝɟbɥıظʞ1ɯuodbɹsʇnʌʍxʎz'.split('');
    var str = str.split('');
    for(var i = 0; i < a.length; i++){
        var ac = a[i];
        str = str.map(function(c){
            if(c == ac) return b[i];
            return c;
        });
    };
    return str.join('');
};

